import { describe, expect, test } from "vitest";
import { Airtable } from "../src";
import { mockResponses } from "./utils";
import { AirtableTable } from "../src/table";

describe("AirtableBase", function () {
  test("instantiate AirtableTable", function () {
    const table = new Airtable()
      .base("appEpvhkjHcG8OvKu")
      .table("tblc7ieWKVQM9eequ");

    expect(table).toBeInstanceOf(AirtableTable);
    expect(table.id).toEqual("tblc7ieWKVQM9eequ");
  });

  describe("List tables - /meta/bases/:id/tables", async function () {
    test("includeVisibleFieldIds true", async function () {
      mockResponses(200, {
        tables: [
          {
            id: "tblc7ieWKVQM9eequ",
            name: "Example Table",
            primaryFieldId: "fldCB4M9WX4ofDKHy",
            fields: [
              {
                type: "singleLineText",
                id: "fldCB4M9WX4ofDKHy",
                name: "Name",
              },
              {
                type: "multilineText",
                id: "fldXemhQhraCWy9zA",
                name: "Notes",
              },
              {
                type: "singleCollaborator",
                id: "fldEbpBsRhClHlYek",
                name: "Assignee",
              },
              {
                type: "singleSelect",
                options: {
                  choices: [
                    {
                      id: "sellIKyVXAgeJC2n8",
                      name: "Todo",
                      color: "redLight2",
                    },
                    {
                      id: "selFIp8DTmQOXcIiP",
                      name: "In progress",
                      color: "yellowLight2",
                    },
                    {
                      id: "selRqqTk913qOahvn",
                      name: "Done",
                      color: "greenLight2",
                    },
                  ],
                },
                id: "fldQpJuVCdQGxZzmh",
                name: "Status",
              },
            ],
            views: [
              {
                id: "viweb85ypN8Z9ZT7F",
                name: "Grid view",
                type: "grid",
                visibleFieldIds: [
                  "fldCB4M9WX4ofDKHy",
                  "fldXemhQhraCWy9zA",
                  "fldEbpBsRhClHlYek",
                  "fldQpJuVCdQGxZzmh",
                ],
              },
            ],
          },
        ],
      });

      const base = new Airtable().base("appEpvhkjHcG8OvKu");
      const schema = await base.schema(true);

      expect(schema).toMatchObject({
        tables: expect.arrayContaining([
          {
            id: expect.any(String),
            name: expect.any(String),
            primaryFieldId: expect.any(String),
            fields: expect.arrayContaining([expect.any(Object)]),
            views: expect.arrayContaining([expect.any(Object)]),
          },
        ]),
      });
    });

    test("includeVisibleFieldIds false", async function () {
      mockResponses(200, {
        tables: [
          {
            id: "tblc7ieWKVQM9eequ",
            name: "Example Table",
            primaryFieldId: "fldCB4M9WX4ofDKHy",
            fields: [
              {
                type: "singleLineText",
                id: "fldCB4M9WX4ofDKHy",
                name: "Name",
              },
              {
                type: "multilineText",
                id: "fldXemhQhraCWy9zA",
                name: "Notes",
              },
              {
                type: "singleCollaborator",
                id: "fldEbpBsRhClHlYek",
                name: "Assignee",
              },
              {
                type: "singleSelect",
                options: {
                  choices: [
                    {
                      id: "sellIKyVXAgeJC2n8",
                      name: "Todo",
                      color: "redLight2",
                    },
                    {
                      id: "selFIp8DTmQOXcIiP",
                      name: "In progress",
                      color: "yellowLight2",
                    },
                    {
                      id: "selRqqTk913qOahvn",
                      name: "Done",
                      color: "greenLight2",
                    },
                  ],
                },
                id: "fldQpJuVCdQGxZzmh",
                name: "Status",
              },
            ],
            views: [
              {
                id: "viweb85ypN8Z9ZT7F",
                name: "Grid view",
                type: "grid",
              },
            ],
          },
        ],
      });

      const base = new Airtable().base("appEpvhkjHcG8OvKu");
      const $schema = await base.schema(false);

      expect($schema).toMatchObject({
        tables: expect.arrayContaining([
          {
            id: expect.any(String),
            name: expect.any(String),
            primaryFieldId: expect.any(String),
            fields: expect.arrayContaining([expect.any(Object)]),
            views: expect.arrayContaining([expect.any(Object)]),
          },
        ]),
      });
    });
  });

  test("Create new table - /meta/bases/:id/tables", async function () {
    mockResponses(200, {
      id: "tblp3f4B23vZSPRIM",
      name: "Table 1",
      primaryFieldId: "fld5Bhc6wm7M1DETy",
      fields: [
        { id: "fld5Bhc6wm7M1DETy", type: "singleLineText", name: "Name" },
      ],
      views: [{ id: "viweb85ypN8Z9ZT7F", type: "grid", name: "Grid view" }],
    });

    const airtable = new Airtable();
    const schema = await airtable.base("appc3aUbML8PZBqjA").createTable({
      name: "Table 1",
      fields: [{ name: "Name", type: "singleLineText" }],
    });

    expect(schema).toMatchObject({
      id: expect.any(String),
      primaryFieldId: expect.any(String),
      name: expect.any(String),
    });
  });

  test("Upload attachment - /:baseId/:recordId/:attachmentFieldIdOrName/uploadAttachment", async function () {
    mockResponses(200, {
      id: "recPaibwSLDbZr80V",
      createdTime: "2024-12-15T04:20:33.000Z",
      fields: {
        fldw7KUUaL5nVAKNU: [
          {
            id: "attz6pHoqq5qTvC32",
            url: `https://v5.airtableusercontent.com/v3/u/36/36/example_url`,
            filename: "new_attachment.png",
            size: 24_543,
            type: "image/png",
          },
        ],
      },
    });

    const airtable = new Airtable();
    const response = await airtable
      .base("appEpvhkjHcG8OvKu")
      .uploadAttachment("recPaibwSLDbZr80V", "fldw7KUUaL5nVAKNU", {
        filename: "new_attachment.png",
        contentType: "image/png",
        file: "data:image/png;base64,",
      });

    expect(response).toMatchObject({
      id: expect.any(String),
      createdTime: expect.any(String),
      fields: expect.objectContaining({
        fldw7KUUaL5nVAKNU: expect.arrayContaining([
          expect.objectContaining({
            id: expect.any(String),
            url: expect.any(String),
            filename: expect.any(String),
            // and more
          }),
        ]),
      }),
    });
  });
});
